#!/usr/bin/env python2
import time
import string
import random

from pwn import *

def main():
    p = remote("localhost", 12312)
    flag = "".join("{:02x}".format(ord(c)) for c in "flag")
    while True:
        strs = "A" * (250 * 2)
        p.sendline(strs.encode('utf-8'))
        result = p.recvline().decode('utf-8')
        print(result)
        if flag in result:
            print(result)
            break
        # time.sleep(1)

    return 0


